mapper.select:
SELECT 

FROM easyshop_brand a

WHERE a.id = #{id}

SELECT 

FROM easyshop_brand a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.brand_name = #{brandName}


		AND a.is_show = #{isShow}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM easyshop_brand a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE easyshop_brand SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
