mapper.select:
SELECT 

FROM easyshop_qs_answer a

WHERE a.id = #{id}

SELECT 

FROM easyshop_qs_answer a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM easyshop_qs_answer a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE easyshop_qs_answer SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
