mapper.select:
SELECT 

FROM easyshop_qs_issue a

WHERE a.id = #{id}

SELECT 

FROM easyshop_qs_issue a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.question_id = #{questionId}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort asc

SELECT 

FROM easyshop_qs_issue a


	a.del_flag = #{DEL_FLAG_NORMAL} 

		AND a.question_id = #{questionId}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort asc

mapper.update:
UPDATE easyshop_qs_issue SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
